import React, { useState } from 'react';

const DateCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const dates = ['Wed 22', 'Thu 23', 'Fri 24', 'Sat 25', 'Sun 26', 'Tue 28', 'Vote', 'Wed 22', 'Thu 23', 'Fri 24', 'Sat 25', 'Sun 26', 'Tue 28', 'Vote'];

  const goToPrevious = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? dates.length - 1 : prevIndex - 1));
  };

  const goToNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === dates.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className="date-carousel">
      <button className="prev-button" onClick={goToPrevious}>
        &lt;
      </button>
      <div className="date-container">
        {dates.map((date, index) => (
          <div
            key={index}
            className={`date-item ${index === currentIndex ? 'active' : ''}`}
          >
            {date}
          </div>
        ))}
      </div>
      <button className="next-button" onClick={goToNext}>
        &gt;
      </button>
    </div>
  );
};

export default DateCarousel;
