import { useState } from 'react';
import triviaData from './trivia-data';
import './trivia_game.css';

function Game() {
    const [gameState, setGameState] = useState({
        score: 0,
        triviaIndex: 0,
        isGameOver: false,
        userAnswer: null,
        correct: null,
    });

    const { score, triviaIndex, isGameOver, userAnswer, correct } = gameState;

    const TriviaQuestion = triviaData[triviaIndex] || {};
    const { correct_answer, incorrect_answers, question } = TriviaQuestion;

    // Handle answer button click
    const handleAnswerClick = (answer) => {
        if (answer === correct_answer) {
            setGameState({
                ...gameState,
                userAnswer: answer,
                correct: true,
                score: score + 1,
            });
        } else {
            setGameState({
                ...gameState,
                userAnswer: answer,
                correct: false,
            });
        }
    };

    // Function to go to the next trivia question
// Function to go to the next trivia question
const handleNextQuestion = () => {
    if (triviaIndex < triviaData.length - 1) {
        setGameState({
            ...gameState,
            triviaIndex: triviaIndex + 1,
            userAnswer: null,
            correct: null,
        });
    } else {
        setGameState({
            ...gameState,
            isGameOver: true,
        });
    }
};


    // Render the component
    return (
        <div className="Game">
            {!isGameOver && TriviaQuestion ? (
                <>
                    <p>{question}</p>
                    <div className='optionbox'>
                        {[correct_answer, ...incorrect_answers].sort().map((answer, index) => (
                            <button key={index} onClick={() => handleAnswerClick(answer)}  className='options'>
                                {answer}
                            </button>
                        ))}
                    </div>
                    {userAnswer && (
                        <p className={correct ? "correct-answer" : "wrong-answer"}>
                            {correct ? "Correct Answer!" : `Wrong Answer! Correct was: ${correct_answer}`}
                        </p>
                    )}
                    <button onClick={handleNextQuestion} className="nextButton">Next Question</button>
                </>
            ) : (
                <p>No more questions or game is over.</p>
            )}
        </div>
    );
}

export default Game;
