import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function MobileMenu() {
    const [isOpen, setIsOpen] = useState(false);
    const menuRef = useRef(); // Create a ref for the menu

    const toggleMenu = (event) => {
        event.stopPropagation(); // Prevent click from bubbling up to the document
        setIsOpen(prevIsOpen => !prevIsOpen); // Correctly toggle based on previous state
    };

    // Handle clicks outside the menu
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                console.log("Click outside, closing menu");
                setIsOpen(false); // Close menu if clicking outside of it
            }
        };

        // Add event listener when the component mounts
        document.addEventListener('mousedown', handleClickOutside);

        // Clean up event listener
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []); // Empty dependency array ensures this runs only on mount and unmount
    return (
        <div>
<button className="menu-button" onClick={toggleMenu}>
    &#9776; {/* Unicode for hamburger icon */}
</button>
            {isOpen && (
                <div className="menu" ref={menuRef}>
                    <ul>
                        <li><a href="#home">Home</a></li>
                        <li><a href="#about">About</a></li>
                        <li><a href="#services">Services</a></li>
                        <li><a href="#contact">Contact</a></li>
                    </ul>
                </div>
            )}
        </div>
    );
}

export default MobileMenu;
