import React from 'react';
import gamesData from './game-data-test';

function GameTiles() {
    return (
        <div className="game-tiles-container">
            {gamesData.map((game, index) => (
                <div className={`game-tile game-tile-${index % 4}`} key={index}>
                    <a href={game.url} className="game-image-link">
                        <img src={game.banner_url} width="100%" alt={`Game Image of ${game.title}`} />
                        <div className="game-title">{game.title || 'Untitled Game'}</div>
                    </a>
                    <button className="game-button" onClick={() => alert(`Clicked on ${game.title}`)}>
                        Play Now!
                    </button>
                </div>
            ))}
        </div>
    );
}

export default GameTiles;
