import React, { useState, useEffect, useRef } from 'react';

const DateCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const dates = ['Wed 22', 'Thu 23', 'Fri 24', 'Sat 25', 'Sun 26', 'Tue 28', 'Vote'];
  const containerRef = useRef(null);

  useEffect(() => {
    const container = containerRef.current;
    const containerWidth = container.offsetWidth;
    const itemWidth = container.firstChild.offsetWidth;
    const maxTranslateX = (dates.length - 1) * itemWidth;

    container.style.transform = `translateX(-${currentIndex * itemWidth}px)`;
    container.style.width = `${dates.length * itemWidth}px`;
  }, [currentIndex, dates.length]);

  const goToPrevious = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? dates.length - 1 : prevIndex - 1));
  };

  const goToNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === dates.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className="date-carousel">
      <button className="prev-button" onClick={goToPrevious}>
        &lt;
      </button>
      <div className="date-container" ref={containerRef}>
        {dates.map((date, index) => (
          <div key={index} className="date-item">
            {date}
          </div>
        ))}
      </div>
      <button className="next-button" onClick={goToNext}>
        &gt;
      </button>
    </div>
  );
};

export default DateCarousel;
