import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function MobileMenu() {
    const [isOpen, setIsOpen] = useState(false);
    const menuRef = useRef(); // Ref for the menu

    const toggleMenu = () => {
        setIsOpen(!isOpen); // Toggles the open state
    };

    // Effect for handling clicks outside the menu
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                setIsOpen(false); // Close the menu if clicking outside of it
            }
        };

        // Add when the menu is open and remove when the menu is closed
        if (isOpen) {
            document.addEventListener('mousedown', handleClickOutside);
        } else {
            document.removeEventListener('mousedown', handleClickOutside);
        }

        // Cleanup function
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, [isOpen]); // Dependency on isOpen to add/remove event listener

    return (
        <div className="app-container">
            <header style={{position: 'relative'}}>
                PlayDaily
                <button className="menu-button" onClick={toggleMenu}>&#9776;</button>
            </header>
            <div className={`menu ${isOpen ? 'open' : ''}`} ref={menuRef}>
                <ul>
                    <li><a href="#home">Home</a></li>
                    <li><a href="#about">About</a></li>
                    <li><a href="#services">Services</a></li>
                    <li><a href="#contact">Contact</a></li>
                </ul>
            </div>
            {/* Other components */}
        </div>
    );
}

export default MobileMenu;
