import React, { useState, useRef, useEffect } from 'react';
import './App.css';  // Ensure your styles are imported

const DateCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const dates = [
    { day: 'Mon', icon: '👾' },
    { day: 'Tue', icon: '🔥' },
    { day: 'Wed', icon: '👟' },
    { day: 'Thu', icon: '🚗' },
    { day: 'Fri', icon: '🍔' },
    { day: 'Sat', icon: '🎉' },
    { day: 'Sun', icon: '🌞' }
  ];

  const generateRandomProgress = () => Math.floor(Math.random() * 101); // Generates a random progress between 0 and 100

  const carouselRef = useRef(null);

  const goToPrevious = () => {
    setCurrentIndex(prevIndex => (prevIndex === 0 ? dates.length - 1 : prevIndex - 1));
  };

  const goToNext = () => {
    setCurrentIndex(prevIndex => (prevIndex === dates.length - 1 ? 0 : prevIndex + 1));
  };

  useEffect(() => {
    const activeItem = carouselRef.current?.children[currentIndex];
    if (activeItem) {
      const scrollLeft = activeItem.offsetLeft - (carouselRef.current.offsetWidth / 2 - activeItem.offsetWidth / 2);
      carouselRef.current.scroll({
        left: scrollLeft,
        behavior: 'smooth'
      });
    }
  }, [currentIndex]);

  return (
    <div className="date-carousel-container">
        <button onClick={goToPrevious} className="carousel-nav carousel-prev">&#10094;</button>
        <div className="date-carousel" ref={carouselRef}>
            {dates.map((date, index) => (
                <div key={index} className={`date-item ${index === currentIndex ? 'active' : ''}`}>
                    <div className="date-icon-container">
                        <svg className="progress-ring" width="60" height="60">
                            <circle
                              className="progress-ring__circle"
                              stroke="green"
                              strokeWidth="4"
                              fill="transparent"
                              r="26"
                              cx="30"
                              cy="30"
                              style={{ strokeDasharray: `${2 * Math.PI * 26}`, strokeDashoffset: `${(1 - generateRandomProgress() / 100) * 2 * Math.PI * 26}` }}
                            />
                        </svg>
                        <span className="date-icon">{date.icon}</span>
                    </div>
                    <span className="date-text">{date.day}</span>
                </div>
            ))}
        </div>
        <button onClick={goToNext} className="carousel-nav carousel-next">&#10095;</button>
    </div>
  );
};

export default DateCarousel;
