import React, { useState, useRef, useEffect } from 'react';

const DateCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const dates = ['Wed 22', 'Thu 23', 'Fri 24', 'Sat 25', 'Sun 2', 'Wed 22', 'Thu 23', 'Fri 24', 'Sat 25', 'Sun 2'];
  const carouselRef = useRef(null);

  const goToPrevious = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? dates.length - 1 : prevIndex - 1));
  };

  const goToNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === dates.length - 1 ? 0 : prevIndex + 1));
  };

  useEffect(() => {
    const activeItem = carouselRef.current?.children[currentIndex];
    if (activeItem) {
      const scrollLeft = activeItem.offsetLeft - (carouselRef.current.offsetWidth / 2 - activeItem.offsetWidth / 2);
      carouselRef.current.scroll({
        left: scrollLeft,
        behavior: 'smooth'
      });
    }
  }, [currentIndex]);

  return (
    <div className="date-carousel" ref={carouselRef}>
      {dates.map((date, index) => (
        <div key={index} className={`date-item ${index === currentIndex ? 'active' : ''}`}>
          {date}
        </div>
      ))}
    </div>
  );
};

export default DateCarousel;
