import React, { useEffect, useState } from 'react';
import axios from 'axios';

function GameTiles() {
    const [games, setGames] = useState([]);

    useEffect(() => {
        // Fetch data from the API
        axios.get('https://api.studio1v1.com/trivia/home/')
            .then(response => {
                console.log('API response:', response.data); // Add this line to debug the response
                if (Array.isArray(response.data)) {
                    setGames(response.data); // Assuming the data structure matches the required one
                } else {
                    console.error('API response is not an array:', response.data);
                }
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }, []);

    return (
        <div className="game-tiles-container">
            {Array.isArray(games) && games.map((game, index) => (
                <div className="game-tile" key={index}>
                    <img src={game.image_url} width="100%" alt="Game Image" />
                    <p>{game.title}</p>
                </div>
            ))}
        </div>
    );
}

export default GameTiles;
